import { Component, Input, EventEmitter, ElementRef } from '@angular/core';


@Component({
    selector: 'ndv-area',
    styles: [`
       #ndv-ic {
        margin-left: 10px;
        color: #c4c4c4;
        }
        .ndv-comp {
            padding:6px;
            border-radius: 3px;
            border: 1px solid rgba(255,255,255, 0.0);;

        }
        .active-ndv {
            background-color: #f0f0f0;
            border: 1px solid #d9d9d9;
        }
        input {
            border-radius: 10px;
            box-shadow: none;
            border: 1px solid #dedede;
            min-width: 5px;
        }
        .ndv-buttons {
            background-color: #f0f0f0;
            border: 1px solid #ccc;
            border-top: none;
            border-radius: 0 0 10px 10px;
            box-shadow: 0 3px 6px rgba(164,164,164,0.2);
            outline: none;
            padding: 3px;
            position: absolute;
            margin-left: 6px;
            z-index: 1;
        }
        .ndv-comp:hover {
            border: 1px solid #c3c3c3;
        }
        .ndv-comp:hover > ndv-ic {
            display:block;
        }
        .ndv-save {
            margin-right:3px;
        }
        .ndv-active {
            background-color: #f0f0f0;
            border: 1px solid #d9d9d9;
        }
    `],
    template: `<span *ngIf="!permission" [innerHTML] = "text"></span><form *ngIf="permission" class='ndv-comp' [ngClass]="{'ndv-active':show}" name='areaForm'><span>
                    <textarea name='area' rows="6" style="width: 100%" *ngIf='show' [(ngModel)]='text'></textarea>
                    <span *ngIf='!show' style='line-height:1.5em;word-wrap: break-word;' (click)='makeEditable()' [innerHTML] = "text"></span>
                    <i id='ndv-ic' *ngIf='!show'>âœŽ</i>

                </span>
                <div class='ndv-buttons' *ngIf='show'>
                   <button class='btn btn-outline-success btn-sm' (click)='callSave()'><i class="fa fa-check-circle" aria-hidden="true"></i></button>
                    <button class='btn btn-outline-danger btn-sm' (click)='cancelEditable()'><i class="fa fa-times" aria-hidden="true"></i></button>
                </div></form>`,
    host: {
        "(document: click)": "compareEvent($event)",
        "(click)": "trackEvent($event)"
    },
    outputs: ['save : onSave']
})

export class NdvEditAreaComponent {
    @Input('placeholder') text;
    @Input('title') fieldName;
    @Input() permission = true;
    originalText;
    tracker;
    el: ElementRef;
    show = false;
    save = new EventEmitter;

    constructor(el: ElementRef) {
        this.el = el;
    }

    ngOnInit() {
        this.originalText = this.text;    //Saves a copy of the original field info.
    }

    makeEditable() {
        if (this.show == false) {
            this.show = true;
        }
    }

    compareEvent(globalEvent) {
        if (this.tracker != globalEvent && this.show) {
            this.cancelEditable();
        }
    }

    trackEvent(newHostEvent) {
        this.tracker = newHostEvent;
    }

    cancelEditable() {
        this.show = false;
        this.text = this.originalText;
    }

    callSave() {
        var data = {};  //BUILD OBJ FOR EXPORT.
        data["" + this.fieldName] = this.text;
        var oldText = this.text;
        setTimeout(() => { this.originalText = oldText; this.text = oldText }, 0);  //Sets the field with the new text;
        this.save.emit(data);
        this.show = false;

    }
}
